import React, {useState, useEffect} from "react";
import { ActivityIndicator, ScrollView, Button, View, Text, FlatList, SafeAreaView, StatusBar, StyleSheet, TouchableOpacity } from "react-native";

export default class Home extends React.Component{

  constructor(props) {
    super(props);

    this.state = {
      data: [],
      isLoading: true
    };
  }

  async getData() {
    try {
      const response = await fetch('http://specsphones.rf.gd/api/products/all');
      const json = await response.json();
      this.setState({ data: json.results });
    } catch (error) {
      console.log(error);
    } finally {
      this.setState({ isLoading: false });
    }
  }

  componentDidMount() {
    this.getData();
  }

render(){

    const { data, isLoading } = this.state;

      <View style={{ flex: 1, padding: 24 }}>
        {isLoading ? <ActivityIndicator/> : (
          <FlatList
            data={data}
            keyExtractor={({ id }, index) => id}
            renderItem={({ item }) => (
              <Text>{item.title}, {item.rates}</Text>
            )}
          />
        )}
      </View>

}

}

const styles = StyleSheet.create({ 
    container: {
        margin: 1,
        padding: 20,
    },

    Title :{

        margin:5,
        fontWeight:'bold',
        fontSize: 24
    }    

})
