import React, { useEffect, useState } from "react";
import {
  Center,
  HStack,
  Text,
  Box,
  Image,
  AspectRatio,
  Heading,
  Spinner,
  FlatList,
} from "native-base";
import { TouchableOpacity } from "react-native";

  const renderItem = ({ item }) => {
    return (
      <TouchableOpacity onPress={() => navigation.navigate("Details", { resepKey: item.key })}>
        <Box p={5}>
          <Box bg={"white"} borderWidth={1} borderColor={"gray.300"}>
            {/* Image Section */}
            <Box>
              <AspectRatio w="100%" ratio={16 / 9}>
                <Image source={{ uri: item.thumb }} alt="" />
              </AspectRatio>
              <Box position={"absolute"} bottom={0} left={0} p={2} bg={"#fe7100"}>
                <Text>{item.dificulty}</Text>
              </Box>
            </Box>

            {/* Text Section */}
            <Box p={3}>
              <Heading>{item.title}</Heading>
              <HStack alignItems={"center"}>
                <Text>{item.times} </Text>
                <Text>{item.portion} </Text>
              </HStack>
            </Box>

          </Box> {/* End tag card */}
        </Box>
      </TouchableOpacity>
    );
  };

export default RecipeScreen;
