import React, {useState, useEffect} from "react";
import { ActivityIndicator, ScrollView, Button, View, Text, FlatList, SafeAreaView, StatusBar, StyleSheet, TouchableOpacity } from "react-native";

export default class Home extends React.Component{

render(){

    const [data, setData] = useState([]);
    const [isLoading, setLoading] = useState(true);


    const getData = async () => {
        try {
            const response = await fetch("http://specsphones.rf.gd/api/products/all");
            const json = await response.json();
            setData(json);
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    const renderItem = ({ item }) => {
        return (
            <TouchableOpacity onPress={() => navigation.navigate('Detail',{data:item.id})}
                style={styles.container}
            >

                <Image source={{uri: item.image }}  style={{width:160, height:160}}/>
                <Text style={styles.Title}>{item.title}</Text>
                <Text style={{fontSize: 15, fontWeight: "bold"}}>Rates: {item.rate}</Text>
            </TouchableOpacity>
        );
    };

    useEffect(()=> {
        getData();

    }, [])

    return(
        <View>
            {
                isLoading ? (
                    <ActivityIndicator size="large" color="#2196F3"/>

                ):(
                    <FlatList
                    data={data}
                    keyExtractor={item => item.id}
                    renderItem={renderItem}/>
                )
            }

        </View>
    )

}

}

const styles = StyleSheet.create({ 
    container: {
        margin: 1,
        padding: 20,
    },

    Title :{

        margin:5,
        fontWeight:'bold',
        fontSize: 24
    }    

})
